css_content:
/* Fonts */
@import url('https://fonts.googleapis.com/css2?family=Inter:ital,opsz,wght@0,14..32,100..900;1,14..32,100..900&display=swap');

/* Root Variables */
:root {
    --primary: #0ea5e9;
}

/* Body Styles */
body {
    font-family: "Inter", sans-serif !important;
    background: #f0f9ff !important;
}

/* Navbar Styles */
.navbar-nav {
    font-weight: 500;
    padding-left: 10px;
    padding-right: 10px;
}

.navbar-nav i.ni {
    color: var(--primary) !important;
}

.navbar-nav .nav-link {
    color: #1e293b !important;
}

.navbar-nav .nav-link.active {
    background-color: var(--primary) !important;
    border-radius: 0.5rem;
    margin-left: 0px;
    margin-right: 10px;
}

.navbar-nav .nav-link.active:before {
    border-left: 0 !important;
}

.navbar-nav .nav-link.active,
.navbar-nav .nav-link.active .ni {
    color: #ffffff !important; 
}

.navbar-brand {
    padding-bottom: 0 !important;
}

.navbar-brand-img {
    max-height: 4rem !important;
}

/* Header Styles */
.header .header-body .btn.btn-outline-primary:hover {
    color: #ffffff !important;
    border-color: var(--primary) !important;
    background: var(--primary) !important;
    box-shadow: 0 1px 2px 0 rgb(0 0 0 / 0.05) !important;
}

.header .header-body .btn.btn-outline-primary {
    margin-top: 10px;
    min-width: 180px;
    border-color: var(--primary) !important;
    background-color: transparent !important;
    color: var(--primary) !important;
}

.header .header-body .card-stats {
    min-height: 142px !important;
    box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
}

/* Card Styles */
.card {
    width: unset !important;
    border-radius: 0.5rem !important;
}

.card.shadow-lg,
.card.shadow {
    box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1) !important;
}

.shadow {
    box-shadow: none !important;
}

/* Navigation Pills */
.nav-pills .nav-link {
    border: 1px solid var(--primary);
    color: var(--primary) !important;
    border-radius: 0.375rem !important;
}

.btn-primary,
.nav-pills .nav-link.active {
    background-color: var(--primary) !important;
    color: #ffffff !important;
    box-shadow: 0 1px 2px 0 rgb(0 0 0 / 0.05) !important;
    border: 0 !important;
}

/* Form Controls */
.form-control {
    box-shadow: none !important;
    padding: 0.5rem 0.75rem !important;
    border-radius: 0.375rem !important;
    border: 1px solid #e2e8f0 !important;
}

textarea.form-control {
    padding: 0.75rem !important;
}

/* Preview Element */
#previewElement,
#previewElement .card {
    min-width: unset !important;
}

/* Gradient Styles */
.bg-gradient-primary,
.bg-gradient-info {
    background: var(--primary) !important;
}

.bg-gradient-success {
    background: var(--primary) !important;
}

.bg-gradient-success .avatar {
    color: var(--primary) !important;
    background-color: #ffffff !important;
}

/* Avatar Styles */
.avatar.bg-gradient-primary {
    min-height: 48px;
    min-width: 48px;
    border-radius: 0.5rem !important;
}

/* Chat List Styles */
#chatList .card-body .d-block {
    cursor: pointer;
}

#chatList .nav-pills .nav-link {
    box-shadow: none !important;
}

#chatList .nav-pills .nav-item {
    padding-right: 0.5rem !important;
}

/* Nav Wrapper Styles */
.nav-wrapper .nav-item .nav-link {
    color: var(--primary) !important;
    box-shadow: none !important;
}

.nav-wrapper .nav-item .nav-link.active {
    color: #ffffff !important;
}

/* Media Queries */
@media only screen and (min-width: 768px) {
    .main-content .navbar-top {
        background: #ffffff !important;
    }
}
